import React, {useEffect, useState} from 'react';
import Navbar from '../../Shared/Navbar';
import DeveiceMeter from '../Locations/DeveiceMeter';

import logo from '../../../Resorces/logo_RLAB.png';
import './DeviceDashBoard.css';

import {BrowserRouter as Router, Switch, Route, Link, useParams} from "react-router-dom";

import db from '../../FirebaseConfig/Firebase'
import {useContext} from 'react';
import {userContext} from '../../../App';

const DeviceDashBoard = () => {
    let {format, id} = useParams();

    const [status, setStatus] = useState([])
    const [loading, setLoading] = useState(true);
    const [pastData, setPastData] = useState([])
    // const [pastData, setPastData] = useState({   present:true,   week:false,
    // day:false,   halfDay:false,   sixH:false,   oneH:false })
    const [user, setUser] = useContext(userContext)

    async function arrayFunc(arr, key) {
        let resultArray = [];
        for (let i = 0; i < arr.length; i++) {
            if (arr[i].dateSocket <= key) {
                resultArray = await arr[i];
            }
        }
        return resultArray
    };

    //  function for liner search start finish |^ linear search shorting data

    useEffect(() => {
        if (user.present === true) {
            const userDb = db
                .collection("RealTime")
                .doc(id)
                .onSnapshot((querySnapshot) => {

                    setStatus({
                        ...querySnapshot.data(),
                        key: querySnapshot.id
                    });

                    // const maxNumber1 = maxNumber(getDataFirebase);

                });
            return userDb;
        } else if (user.week === true) {
            const userDb2 = db.collection(id).orderBy('dateSocket', 'desc').where('').onSnapshot((querySnapshot) => {
                    let getDataFirebase = [];
                    querySnapshot.forEach((doc) => {
                        getDataFirebase.push({...doc.data(),key: doc.id});
                    });
                    let UnixTimeFOrToday = Date.now() / 1000;
                    let unixToWeek = UnixTimeFOrToday - 604800;
                    
                    const Aweek = arrayFunc(getDataFirebase, unixToWeek)
                    setPastData(Aweek)
                    console.log({Aweek})
                    return userDb2
                });
           
    

        }

    }, [loading, format, id, user]);
    
    console.log('dateSocket', 1627894069369 - 6.048e+8)
    console.log({pastData})
    return (
        <div className='row'>
            <div className="col-md-1">
                <Navbar/>
            </div>
            <div className="col-md-11 text-center">
                <img
                    src={logo}
                    alt=""
                    style={{
                        width: '20%'
                    }}
                    className='mt-5'/>

                <DeveiceMeter data={status} pastDataTrig={user} setProps={user}/>
            </div>
        </div>
    );
};

export default DeviceDashBoard;
