import React, { useEffect, useState } from 'react';
import Navbar from '../../Shared/Navbar';
import './UserHome.css'
import User from '../User/User'
import logo from '../../../Resorces/logo_RLAB.png';


import db from '../../FirebaseConfig/Firebase'
import MenuIcon from '@material-ui/icons/Menu';
// function getWindowDimensions() {
//     const {innerWidth: width, innerHeight: height} = window;
//     return {width, height};
// }

const UserHome = () => {
   
    
    const [dbUserData, setDbUserData] = useState([]);
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
    const [sideMenu, setSideMenu] = useState({
        click:false,
        notClick: true,
    })
    useEffect(() => {
        const getDataFirebase = [];
        const userDb = db.collection("user").onSnapshot((querySnapshot) => {
            querySnapshot.forEach((doc) => {
              getDataFirebase.push({...doc.data(), key:doc.id});
            });
            setDbUserData(getDataFirebase);
            
        });
        
       return userDb;
    }, []);
   
    // useEffect(() => {
    //     function handleResize() {
    //         setWindowDimensions(getWindowDimensions());
    //     }

    //     window.addEventListener('resize', handleResize);
    //     return() => window.removeEventListener('resize', handleResize);
    // }, []); 

    const handleClickSideMenu = (event) => {
        const sideClick = {...sideMenu};
        sideClick.click = true;
        sideClick.notClick = false;
        setSideMenu(sideClick);
        
        event.preventDefault()
        
    }
    console.log({sideMenu})
    return (
        <div className='row'>
       
             <div className="col-md-1">
             <div className="container pt-5" >
             <button onClick={handleClickSideMenu}>Epand</button>
             <MenuIcon />
             </div>
                 { <Navbar/>}
                 {sideMenu.click === true && <Navbar/>}
            </div>
            <div className="col-md-12 backgroundSIDE text-center" style={{position:'absolute'}}>
            
            
            <img src={logo} alt="" style={{width:'20%'}} className='mt-5' />
                {/* <User data={dbUserData}/> */}
            </div>

                
               
                
            
        </div>
    );
};

export default UserHome;
