import React, {useEffect, useState} from 'react';
import './user.css';
import '../../../responsive.css';
import UserForm from './UserForm';

import UserData from './UserData';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
  }

const User = (props) => {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());


    const [click, setClick] = useState({clickToUser: true, clickToFrom: false});
    const handleClick = () => {
        const newClick = {
            ...click
        }
        newClick.clickToFrom = true;
        newClick.clickToUser = false;
        setClick(newClick)
    }
 useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);



    const data = props.data
    var x = window.width;
    console.log({windowDimensions})
    return (
        <div className='mt-5 text-center'>
            {
                click.clickToUser && windowDimensions.width >= 720 && <div>
                        <div className="user-map">
                            <div className="d-flex justify-content-center">
                                <table class="content-table">
                                    <thead>
                                        <tr className='text-center'>
                                            <th>Name</th>
                                            <th>Role</th>
                                            <th>Position</th>
                                            <th>Email</th>
                                            <th>UserID</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {
                                            data.map((data) => {
                                                return <tr>
                                                    
                                                    <td>{data.name}</td>
                                                    <td>{data.Role}</td>
                                                    <td>{data.position}</td>
                                                    <td>{data.email}</td>
                                                    <td>{data.Uid}</td>
                                                </tr>
                                            })
                                        }
                                    </tbody>

                                </table>
                                
                            </div>
                            <button className="btn btn-success" onClick={handleClick}>Add User</button>
                           

                        </div>

                    </div>
            }
            {
                click.clickToUser && windowDimensions.width < 720 && <div>
                        <div className="user-map">
                            <div className="d-flex justify-content-center">
                                <table class="content-table">
                                    <thead>
                                        <tr className='text-center'>
                                        <th>Details</th>
                                            <th>Name</th>
                                            <th>Role</th>
                                        </tr>
                                    </thead>
                                    <tbody>

                                        {
                                            data.map((data) => {
                                                return 
                                                <tr>
                                                <ArrowForwardIosIcon/>
                                                    <td>{data.name}</td>
                                                    <td>{data.Role}</td>
                                                    <div class="card" style={{width: '18rem'}}>
  <img class="card-img-top" src="..." alt="Card image cap"/>
  <div class="card-body">
    <h5 class="card-title">Card title</h5>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    <a href="#" class="btn btn-primary">Go somewhere</a>
  </div>
</div>
                                                </tr>
                                                
                                                
                                            })
                                        }
                                    </tbody>

                                </table>
                                
                            </div>
                            <button className="btn btn-success" onClick={handleClick}>Add User</button>
                           

                        </div>

                    </div>
            }
            <div>
                {click.clickToFrom && <UserForm/>}
            </div>
        </div>
    );
};

export default User;
