import React, { useContext, useEffect, useState } from 'react';
import Navbar from '../../Shared/Navbar';
import './UserHome.css'
import User from '../User/User'
import logo from '../../../Resorces/logo_RLAB.png';
import { userContext } from '../../../App';
import Rechart from './Rechart';
import Devices from '../Devices/Devices';
import Location from '../Locations/Locations';
import Navbar2 from '../../Shared/Navbar/Navbar'
import Command from '../Command/Command';
import db from '../../FirebaseConfig/Firebase'
import UserData from '../User/UserData';

const UserHome = () => {
   
    const [user] = useContext(userContext);
    
    const [dbUserData, setDbUserData] = useState({});
    const [loading, setLoading] = useState(true)
    const [menu, setMenu ] = useState({
        showMenu:true,
        hideMenu:false,
        suspendMenu: false,
    })
    useEffect(() => {
        const getDataFirebase = [];
        const userDb = db.collection("user").onSnapshot((querySnapshot) => {
            querySnapshot.forEach((doc) => {
              getDataFirebase.push({...doc.data(), key:doc.id});
            });
            setDbUserData(getDataFirebase);
            setLoading(false)
        });
        
       return userDb;
    }, [setLoading]);
   
    const [click, setClick] = useState({
        clickToUser: true,
        clickToFrom:false
    });
    const handleClick = () => {
        const newClick = {...click}
        newClick.clickToFrom = true;
        newClick.clickToUser = false;
        setClick(newClick)
    }

    return (
        <div className='row'>
            <div className="col-md-1">
                 <Navbar/>
            </div>
            <div className="col-md-11 backgroundSIDE text-center">
            
            <img src={logo} alt="" style={{width:'20%'}} className='mt-5' />
                 {click.clickToUser && <div>
              <div className="user-map">
                    {
                        dbUserData.map((data) =>  {return<UserData  data={data}/>})
                    }
                </div>
            <div className="button d-flex justify-content-end">
                <button className="btn btn-primary" onClick={handleClick}>Add User</button>
            </div> 
           </div>}
            </div>
        </div>
    );
};

export default UserHome;
