import React, { useState } from 'react';
import './user.css';
import '../../../responsive.css';
import UserForm from './UserForm';

import UserData from './UserData';

const User = (props) => {
   
    const [click, setClick] = useState({
        clickToUser: true,
        clickToFrom:false
    });
    const handleClick = () => {
        const newClick = {...click}
        newClick.clickToFrom = true;
        newClick.clickToUser = false;
        setClick(newClick)
    }
    
    const data = props.data;
    return (
        <div className='mt-5 text-center'>
          {click.clickToUser && <div>
              <div className="user-map">
              <table class="content-table">
  <thead>
    <tr>
      <th>Name</th>
      <th>Role</th>
      <th>Position</th>
      <th>Email</th>
      <th>UserID</th>
    </tr>
  </thead>
  <tbody>
    {/* <tr>
      <td>1</td>
      <td>Domenic</td>
      <td>88,110</td>
      <td>dcode</td>
    </tr>
    <tr class="active-row">
      <td>2</td>
      <td>Sally</td>
      <td>72,400</td>
      <td>Students</td>
    </tr>
    <tr>
      <td>3</td>
      <td>Nick</td>
      <td>52,300</td>
      <td>dcode</td>
    </tr> */}
    {
        data.map((data) => {return <tr><td>{data.name}</td> <td>{data.Role}</td><td>{data.email}</td><td>{data.Uid}</td></tr>})
    }
  </tbody>
</table>
                    {
                        data.map((data) =>  <UserData key={data.key}  data={data}/>)
                    }
                    <div className="button d-flex justify-content-end">
                <button className="btn btn-dark" onClick={handleClick}>Add User</button>
            </div> 
                </div>
           
           </div>}
            <div>
                {click.clickToFrom && <UserForm/>}
            </div>
        </div>
    );
};

export default User;
