import React from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../../Shared/Navbar';
import DeviceForm from './DevicesForm';
import MeaSpinner from './MetarialSpinner'
import './DeviceMeter.css'
import { useEffect } from 'react';
import { useState } from 'react';
import db from '../../FirebaseConfig/Firebase';
import { Link } from 'react-router-dom';
import EditIcon from '@material-ui/icons/Edit';
const LocationDevice = () => {
    const {Lid} = useParams();
    console.log({Lid});
    const [DBLocationDV, setDBLocationDV] = useState({})
    useEffect(() => {
        var docRef = db.collection("location").doc(Lid);

        docRef.get().then((doc) => {
            if (doc.exists){
                setDBLocationDV({...doc.data(), key:doc.id});
            } else {
                // doc.data() will be undefined in this case
                console.log("No such document!");
            }
        }).catch((error) => {
            console.log("Error getting document:", error);
        });
    }, [Lid])
    console.log({DBLocationDV});
    return (
        <div className='row'>
            <div className="col-md-1">
                <Navbar/>
            </div>
            <div className="col-md-11">
                {/* <DeviceForm data={DBLocationDV}/> */}
                <div className="container device-form-container mt-5">
                
               <div data-aos="fade-left"  className="clients d-flex justify-content-evenly mb-5">
                    <div className="form-name">{!DBLocationDV.Device_id_1 && <span><MeaSpinner/></span>}<p className="">{DBLocationDV.Device_id_1}</p></div>
                    <div title="Edit" className="meta_icon">
                    <EditIcon />
                    </div>
                    <Link title="select" to={`/locationsDevice/${device1.key}`}  className="meta_icon2"><ArrowForwardIcon/></Link>
                </div>
                <div data-aos="fade-right"  className="clients d-flex justify-content-evenly mb-5">
                    <div className="form-name">{!Device_id_1 && <span><MeaSpinner/></span>}<p className="form-name-p">{DBLocationDV.Device_id_2}</p></div>
                     
                    <div title="Edit" className="meta_icon">
                    <EditIcon />
                    </div>
                    <Link title="select" to={`/locationsDevice/${device1.key}`}   className="meta_icon2"><ArrowForwardIcon/></Link>
                </div>
                <div data-aos="fade-left"  className="clients d-flex justify-content-evenly mb-5">
                    <div className="form-name">{!Device_id_1 && <span><MeaSpinner/></span>}<p className="form-name-p">{DBLocationDV.Device_id_3}</p></div>
                     
                    <div title="Edit" className="meta_icon">
                    <EditIcon />
                    </div>
                    <Link title="select" to={`/locationsDevice/${device1.key}`}  className="meta_icon2"><ArrowForwardIcon/></Link>
                </div>
                <div data-aos="fade-right" className="d-flex justify-content-end buttonArea">
                    <button className="btn btn-dark AddBUtton">Edit Location</button>
                </div>
                </div>
            </div>
        </div>
    );
};

export default LocationDevice;
